definition.script:
node {
  stage('Preparation') {
    if (isUnix()) {
      //sh "REPO_NAME=bsg-ucsf svn co svn+ssh://subversion.assembla.com/trunks/islj"
      sh "svn co --username gepr_github_export --password  https://subversion.assembla.com/svn/bsg-ucsf/trunks/islj"
    }
  }
  stage('Build') {
    if (isUnix()) {
      sh "cd ${WORKSPACE}/islj; ant -Dplatforms.Open_JDK_1.8.home=/usr/lib/jvm/java-8-openjdk-amd64 compile"
    }
  }
  stage('Execute') {
    if (isUnix()) {
      sh "cd ${WORKSPACE}/islj; ./bin/run.sh; ant -Dplatforms.Open_JDK_1.8.home=/usr/lib/jvm/java-8-openjdk-amd64 clean"
    }
  }
  stage('Test') {
      if (isUnix()) {
        EXPDIR = sh (returnStdout: true, script: 'cd ${WORKSPACE}/islj; ls -d 201?-??-??-*').trim()
        r "library(methods); source(\"~/local/scripts/LOPAnalysis.R\"); source(\"~/local/scripts/distance.R\"); lop <- read.LOP(path=\"${WORKSPACE}/islj/${EXPDIR}\",ref.pattern=\"datModel\"); calcSimilarity(lop);"
        sh "rm -rf ${WORKSPACE}/islj/${EXPDIR}"
      }
  }
}
